import type { JestConfigWithTsJest } from 'ts-jest'

const jestConfig: JestConfigWithTsJest = {
  testEnvironment: 'node',
  transform: {
    '^.+\\.ts$': ['ts-jest', { useESM: true }],
  },
}

export default jestConfig

import { ChatCompletion, Retry, createChatClient } from '../create-chat-client'
import { rest } from 'msw'
import { setupServer } from 'msw/node'

const server = setupServer()

beforeAll(() => server.listen())
afterEach(() => server.resetHandlers())
afterAll(() => server.close())

describe('parse', () => {
  it('supports parsing JSON', async () => {
    const testResponse = {
      choices: [
        {
          message: {
            role: 'assistant',
            content: JSON.stringify({
              foo: 1,
              bar: 2,
              baz: ['quux'],
            }),
          },
        },
      ],
    }

    server.use(
      rest.post('*', (_req, res, ctx) => {
        return res(ctx.json(testResponse))
      }),
    )

    type ExampleType = {
      foo: number
      bar: number
      baz: string[]
    }

    const gptClient = createChatClient({
      modelId: 'gpt-4',
      parse: (completion: ChatCompletion): ExampleType => {
        return JSON.parse(completion.choices[0].message.content ?? '')
      },
    })

    const result = await gptClient.createCompletion({
      messages: [
        {
          role: 'user',
          content: 'Test content',
        },
      ],
    })

    expect(result).toEqual({
      foo: 1,
      bar: 2,
      baz: ['quux'],
    })
  })

  it('supports retrying with feedback while under max retries (default of 2)', async () => {
    const testResponse = {
      choices: [
        {
          message: {
            role: 'assistant',
            content: JSON.stringify({
              foo: 1,
              bar: 2,
              baz: ['quux'],
            }),
          },
        },
      ],
    }

    let callCount = 0

    server.use(
      rest.post('*', (_req, res, ctx) => {
        if (callCount < 2) {
          callCount++

          return res(
            ctx.json({
              choices: [
                {
                  text: 'This will blow up JSON.parse',
                },
              ],
            }),
          )
        }

        return res(ctx.json(testResponse))
      }),
    )

    type ExampleType = {
      foo: number
      bar: number
      baz: string[]
    }

    const gptClient = createChatClient<ExampleType>({
      modelId: 'gpt-4',
      parse: async (completion: ChatCompletion, retry: Retry<ExampleType>) => {
        try {
          const json = JSON.parse(completion.choices[0].message.content ?? '')
          return json as ExampleType
        } catch (error) {
          return retry({
            feedback:
              'Hey, GPT-4! Please return the output in the right format!',
            updatedModelParams: {
              temperature: 0,
            },
          })
        }
      },
    })

    const result = await gptClient.createCompletion({
      messages: [
        {
          role: 'user',
          content: 'Test content',
        },
      ],
    })

    expect(result).toEqual({
      foo: 1,
      bar: 2,
      baz: ['quux'],
    })
  })
})

import { RetryStrategy, createChatClient, ChatCompletionMessageParam } from '..'
import { rest } from 'msw'
import { setupServer } from 'msw/node'

const server = setupServer()

beforeAll(() => server.listen())
afterEach(() => server.resetHandlers())
afterAll(() => server.close())

describe('Retry', () => {
  it('should retry on status >= 400 by default', async () => {
    const successResponse = {
      choices: [{ message: { role: 'assistant', content: 'Test response' } }],
    }

    let callCount = 0
    let httpStatuses = [400, 401, 429, 500, 502]

    server.use(
      rest.post('*', (_req, res, ctx) => {
        if (callCount < httpStatuses.length - 1) {
          callCount++
          return res(ctx.status(httpStatuses[callCount]))
        }

        return res(ctx.json(successResponse))
      }),
    )

    const gptClient = createChatClient({
      modelId: 'gpt-4',
      retryStrategy: {
        maxRetries: 5,
      },
    })

    const result = await gptClient.createCompletion({
      messages: mockMessages,
    })

    expect(result).toEqual(successResponse.choices[0].message.content)
  })

  it('should retry with custom strategy', async () => {
    const successResponse = {
      choices: [{ message: { role: 'assistant', content: 'Test response' } }],
    }

    let callCount = 0

    server.use(
      rest.post('*', (_req, res, ctx) => {
        if (callCount < 2) {
          callCount++
          return res(ctx.status(500))
        }

        return res(ctx.json(successResponse))
      }),
    )

    const retryStrategy: RetryStrategy = {
      shouldRetry: (error) => error.status === 500,
      calculateDelay: (retryCount) => 1000 * Math.max(retryCount, 1),
      maxRetries: 2,
    }

    const gptClient = createChatClient({
      modelId: 'gpt-4',
      retryStrategy,
    })

    const result = await gptClient.createCompletion({
      messages: mockMessages,
    })

    expect(result).toBe(successResponse.choices[0].message.content)
  })

  it('should fail after max retries exceeded', async () => {
    server.use(
      rest.post('*', (_req, res, ctx) => {
        return res(ctx.status(429))
      }),
    )

    const gptClient = createChatClient({
      modelId: 'gpt-4',
      retryStrategy: {
        maxRetries: 1,
      },
    })

    const result = gptClient.createCompletion({
      messages: mockMessages,
    })

    await expect(result).rejects.toThrow()
  })
})

const mockMessages: ChatCompletionMessageParam[] = [
  {
    role: 'user',
    content: 'Test content',
  },
]

import {
  createChatClient,
  ChatCompletionMessageParam,
} from '../create-chat-client'
import { rest } from 'msw'
import { setupServer } from 'msw/node'

const server = setupServer()

beforeAll(() => server.listen())
afterEach(() => server.resetHandlers())
afterAll(() => server.close())

describe('trimTokens', () => {
  it('trims messages when token count exceeds maximum allowed for the model', async () => {
    let requestMessages

    server.use(
      rest.post('*', async (req, res, ctx) => {
        requestMessages = (await req?.json()).messages
        return res(
          ctx.json({
            choices: [
              {
                message: {
                  role: 'assistant',
                  content: 'This is a test response',
                },
              },
            ],
          }),
        )
      }),
    )

    const gptClient = createChatClient({
      modelId: 'gpt-4',
      trimTokens: (messages, _overage) => messages.slice(1), // Drop first message
    })

    const messages: ChatCompletionMessageParam[] = [
      MESSAGE_WITH_8192_TOKENS,
      {
        role: 'user',
        content: 'This is a normal message',
      },
    ]

    const result = await gptClient.createCompletion({
      messages,
    })

    expect(requestMessages).toEqual(messages.slice(1))
    expect(result).toBe('This is a test response')
  })

  it('takes into account minResponseTokens if passed', async () => {
    let requestMessages

    server.use(
      rest.post('*', async (req, res, ctx) => {
        requestMessages = (await req?.json()).messages
        return res(
          ctx.json({
            choices: [
              {
                message: {
                  role: 'assistant',
                  content: 'This is a test response',
                },
              },
            ],
          }),
        )
      }),
    )

    const gptClient = createChatClient({
      modelId: 'gpt-3.5-turbo',
      trimTokens: (messages, _overage) => messages.slice(1), // Drop oldest message
      minResponseTokens: 3098,
    })

    // Since gpt-3.5-turbo allows 4097 tokens, and we have min response tokens of 3097, we have 999 left to work with
    const messages: ChatCompletionMessageParam[] = [
      {
        role: 'user',
        content: SINGLE_TOKEN_WORD.repeat(100 - 6),
      },
      {
        role: 'user',
        content: SINGLE_TOKEN_WORD.repeat(900 - 6),
      },
    ]

    // Our trim tokens strategy will drop the oldest messages
    // so we should end up calling OpenAI with just the second message and get a successful response

    const result = await gptClient.createCompletion({
      messages,
    })

    expect(requestMessages).toEqual(messages.slice(1))
    expect(result).toBe('This is a test response')
  })

  it('throws an error when messages still exceed the token limit even after trimming', async () => {
    server.use(
      rest.post('*', (_req, res, ctx) => {
        return res(
          ctx.json({
            choices: [
              {
                message: {
                  role: 'assistant',
                  content: 'This is a test response',
                },
              },
            ],
          }),
        )
      }),
    )

    const gptClient = createChatClient({
      modelId: 'gpt-4',
      trimTokens: (messages, _overage) => messages, // don't drop any tokens
    })

    const messages: ChatCompletionMessageParam[] = [
      MESSAGE_WITH_8192_TOKENS,
      MESSAGE_WITH_8192_TOKENS,
    ]

    await expect(
      gptClient.createCompletion({
        messages,
      }),
    ).rejects.toThrow(
      /Token count \(\d+\) exceeds max tokens per request \(8192\)/,
    )
  })

  it('throws an error when messages still exceed the token limit even after trimming, taking into account minResponseTokens', async () => {
    server.use(
      rest.post('*', (_req, res, ctx) => {
        return res(
          ctx.json({
            choices: [
              {
                message: {
                  role: 'assistant',
                  content: 'This is a test response',
                },
              },
            ],
          }),
        )
      }),
    )

    const gptClient = createChatClient({
      modelId: 'gpt-4',
      trimTokens: (messages, _overage) => {
        if (messages.length > 1) {
          return messages.slice(1)
        }

        return messages
      },
      minResponseTokens: 6000,
    })

    const messages: ChatCompletionMessageParam[] = [MESSAGE_WITH_8192_TOKENS]

    await expect(
      gptClient.createCompletion({
        messages,
      }),
    ).rejects.toThrow(
      /Token count \(\d+\) exceeds max tokens per request \(8192\)/,
    )
  })

  it('messages are not trimmed if trimTokens is not passed', async () => {
    server.use(
      rest.post('*', async (req, res, ctx) => {
        const requestJson = await req.json()
        if ((requestJson.messages ?? []).length > 1) {
          return res(ctx.status(400))
        } else {
          // Shouldn't get here
          return res(
            ctx.json({
              choices: [
                {
                  message: {
                    role: 'assistant',
                    content: 'This is a test response',
                  },
                },
              ],
            }),
          )
        }
      }),
    )

    const gptClient = createChatClient({
      modelId: 'gpt-4',
    })

    const messages: ChatCompletionMessageParam[] = [
      MESSAGE_WITH_8192_TOKENS,
      MESSAGE_WITH_8192_TOKENS,
    ]

    await expect(
      gptClient.createCompletion({
        messages,
      }),
    ).rejects.toThrow('400 status code (no body)')
  })
})

const SINGLE_TOKEN_WORD = 'hey'

// The formatting takes up 7 tokens
const MESSAGE_WITH_8192_TOKENS: ChatCompletionMessageParam = {
  role: 'user',
  content: 'hey' + SINGLE_TOKEN_WORD.repeat(8185),
}

import { encode } from 'gpt-3-encoder'
import OpenAI from 'openai'

export type ChatCompletion = OpenAI.Chat.Completions.ChatCompletion

export type ChatCompletionMessageParam =
  OpenAI.Chat.Completions.ChatCompletionMessageParam

export type ModelParams = Omit<
  OpenAI.Chat.Completions.ChatCompletionCreateParamsNonStreaming,
  'model' | 'messages'
>

// DEFAULT PARSER
export function createChatClient(
  params: CreateChatClientWithDefaultParserParams,
): {
  createCompletion(request: {
    messages: OpenAI.Chat.Completions.ChatCompletionMessageParam[]
    functions?: OpenAI.Chat.ChatCompletionCreateParams.Function[]
    modelParams?: ModelParams
  }): Promise<string | null>
}

// CUSTOM PARSER WITHOUT RETRY
export function createChatClient<TParsedResponse>(
  params: CreateChatClientWithCustomParserParams<
    ResponseParserWithoutRetry<TParsedResponse>
  >,
): {
  createCompletion(request: {
    messages: OpenAI.Chat.Completions.ChatCompletionMessageParam[]
    functions?: OpenAI.Chat.ChatCompletionCreateParams.Function[]
    modelParams?: ModelParams
  }): Promise<TParsedResponse>
}

// CUSTOM PARSER WITH RETRY
export function createChatClient<TParsedResponse>(
  params: CreateChatClientWithCustomParserParams<
    ResponseParserWithRetry<TParsedResponse>
  >,
): {
  createCompletion(request: {
    messages: OpenAI.Chat.Completions.ChatCompletionMessageParam[]
    functions?: OpenAI.Chat.ChatCompletionCreateParams.Function[]
    modelParams?: ModelParams
  }): Promise<TParsedResponse>
}

export function createChatClient<TParsedResponse>(
  params:
    | CreateChatClientWithCustomParserParams<
        ResponseParserWithRetry<TParsedResponse>
      >
    | CreateChatClientWithCustomParserParams<
        ResponseParserWithoutRetry<TParsedResponse>
      >
    | CreateChatClientWithDefaultParserParams,
) {
  const openAiApiKey = params.apiKey ?? process.env.OPENAI_API_KEY
  if (!openAiApiKey) {
    throw new Error(
      'OpenAI API key is required. You must either pass apiKey or set the OPENAI_API_KEY environment variable.',
    )
  }

  const openAiClient = new OpenAI({
    apiKey: openAiApiKey,
    maxRetries: 0,
  })

  const retryDelay = (retryCount: number) => {
    if (IS_TEST) {
      return 0
    }

    // Return time to delay in milliseconds
    return params.retryStrategy?.calculateDelay
      ? params.retryStrategy.calculateDelay(retryCount)
      : 2 ** retryCount * 1000 + // Exponential backoff: 1 second, then 2, 4, 8 etc
          (Math.floor(Math.random() * 50) + 1) // Additional random delay between 1 and 50 ms
  }

  const shouldRetry = (error: any) => {
    return params.retryStrategy?.shouldRetry
      ? params.retryStrategy.shouldRetry(error)
      : !!error.status && error.status >= 400
  }

  const updateModelParamsForRetry = (modelParams: ModelParams) => {
    if (!params.retryStrategy?.updateModelParams) {
      return modelParams
    }

    return params.retryStrategy.updateModelParams(modelParams)
  }

  const callOpenAiWithRetry = (
    request: {
      messages: OpenAI.Chat.Completions.ChatCompletionMessageParam[]
      functions?: OpenAI.Chat.ChatCompletionCreateParams.Function[]
      modelParams?: ModelParams
    },
    options?: Parameters<typeof openAiClient.chat.completions.create>[1],
  ) => {
    let retryCount = 0

    async function makeRequest(modelParams: ModelParams) {
      try {
        const body = {
          model: params.modelId,
          messages: request.messages,
          functions: request.functions,
          ...modelParams,
        }

        return await openAiClient.chat.completions.create(body, options)
      } catch (err) {
        if (!(err instanceof OpenAI.APIError) && !IS_TEST) {
          throw err
        }

        if (retryCount >= (params.retryStrategy?.maxRetries ?? 2)) {
          throw err
        }

        if (shouldRetry(err)) {
          retryCount++

          const delay = retryDelay(retryCount)
          await new Promise((r) => setTimeout(r, delay))

          const updatedModelParams = updateModelParamsForRetry(modelParams)

          return makeRequest(updatedModelParams)
        }

        throw err
      }
    }

    const modelParamsForInitialRequest: ModelParams = {
      ...params.modelDefaultParams,
      ...request.modelParams,
    }

    return makeRequest(modelParamsForInitialRequest)
  }

  if ('parse' in params) {
    if (isResponseParserWithRetry<TParsedResponse>(params.parse)) {
      return createChatClientWithCustomParserWithRetry<TParsedResponse>(
        params as CreateChatClientWithCustomParserParams<
          ResponseParserWithRetry<TParsedResponse>
        >,
        callOpenAiWithRetry,
      )
    } else {
      return createChatClientWithCustomParserWithoutRetry<TParsedResponse>(
        params as CreateChatClientWithCustomParserParams<
          ResponseParserWithoutRetry<TParsedResponse>
        >,
        callOpenAiWithRetry,
      )
    }
  }

  return createChatClientWithDefaultParser(params, callOpenAiWithRetry)
}

export function createChatClientWithCustomParserWithRetry<TParsedResponse>(
  params: CreateChatClientWithCustomParserParams<
    ResponseParserWithRetry<TParsedResponse>
  >,
  callOpenAiWithRetry: CallOpenAiWithRetry,
) {
  const {
    modelId,
    modelDefaultParams = {
      temperature: 0,
      top_p: 1,
      frequency_penalty: 0,
      presence_penalty: 0,
    },
    parse,
    trimTokens,
    minResponseTokens = 0,
  } = params

  const tokenTrimmer = trimTokens ? makeTokenTrimmer(trimTokens) : null

  const createCompletion = async (
    request: {
      messages: OpenAI.Chat.Completions.ChatCompletionMessageParam[]
      functions?: OpenAI.Chat.ChatCompletionCreateParams.Function[]
      modelParams?: ModelParams
    },
    __parseRetryCount = 0,
  ): Promise<TParsedResponse> => {
    const { messages, functions, modelParams } = request

    const trimmedMessages = tokenTrimmer
      ? tokenTrimmer(messages, modelId, minResponseTokens)
      : messages

    const chatCompletion = await callOpenAiWithRetry({
      messages: trimmedMessages,
      functions,
      ...modelDefaultParams,
      ...modelParams,
    })

    if (chatCompletion.choices.length === 0) {
      throw new Error('Empty response from OpenAI')
    }

    const retry = ({
      feedback,
      updatedModelParams,
    }: {
      feedback?: string
      updatedModelParams?: ModelParams
    }) => {
      const feedbackMessage: OpenAI.Chat.Completions.ChatCompletionMessageParam =
        {
          role: 'system',
          content: feedback ?? '',
        }

      const newRequest = {
        messages: feedback ? [...messages, feedbackMessage] : messages,
        modelParams: updatedModelParams
          ? { ...modelParams, ...updatedModelParams }
          : modelParams,
      }

      return createCompletion(newRequest, __parseRetryCount + 1)
    }

    const parsedResponse = await parse(chatCompletion, retry)

    return parsedResponse
  }

  return {
    createCompletion,
  }
}

export function createChatClientWithCustomParserWithoutRetry<TParsedResponse>(
  params: CreateChatClientWithCustomParserParams<
    ResponseParserWithoutRetry<TParsedResponse>
  >,
  callOpenAiWithRetry: CallOpenAiWithRetry,
) {
  const {
    modelId,
    modelDefaultParams = {
      temperature: 0,
      top_p: 1,
      frequency_penalty: 0,
      presence_penalty: 0,
    },
    parse,
    trimTokens,
    minResponseTokens = 0,
  } = params

  const tokenTrimmer = trimTokens ? makeTokenTrimmer(trimTokens) : null

  const createCompletion = async (
    request: {
      messages: OpenAI.Chat.Completions.ChatCompletionMessageParam[]
      functions?: OpenAI.Chat.ChatCompletionCreateParams.Function[]
      modelParams?: ModelParams
    },
    __parseRetryCount = 0,
  ): Promise<TParsedResponse> => {
    const { messages, functions, modelParams } = request

    const trimmedMessages = tokenTrimmer
      ? tokenTrimmer(messages, modelId, minResponseTokens)
      : messages

    const chatCompletion = await callOpenAiWithRetry({
      messages: trimmedMessages,
      functions,
      ...modelDefaultParams,
      ...modelParams,
    })

    if (chatCompletion.choices.length === 0) {
      throw new Error('Empty response from OpenAI')
    }

    return parse(chatCompletion)
  }

  return {
    createCompletion,
  }
}

export function createChatClientWithDefaultParser(
  params: CreateChatClientWithDefaultParserParams,
  callOpenAiWithRetry: CallOpenAiWithRetry,
) {
  const {
    modelId,
    modelDefaultParams = {
      temperature: 0,
      top_p: 1,
      frequency_penalty: 0,
      presence_penalty: 0,
    },
    trimTokens,
    minResponseTokens = 0,
  } = params

  const tokenTrimmer = trimTokens ? makeTokenTrimmer(trimTokens) : null

  const createCompletion = async (
    request: {
      messages: OpenAI.Chat.Completions.ChatCompletionMessageParam[]
      modelParams?: ModelParams
    },
    __parseRetryCount = 0,
  ): Promise<string | null> => {
    const { messages, modelParams } = request

    const trimmedMessages = tokenTrimmer
      ? tokenTrimmer(messages, modelId, minResponseTokens)
      : messages

    const chatCompletion = await callOpenAiWithRetry({
      messages: trimmedMessages,
      ...modelDefaultParams,
      ...modelParams,
    })

    if (chatCompletion.choices.length === 0) {
      throw new Error('Empty response from OpenAI')
    }

    return chatCompletion.choices[0].message.content
  }

  return {
    createCompletion,
  }
}

const makeTokenTrimmer =
  (trimTokens: TrimTokens) =>
  (
    originalMessages: OpenAI.Chat.Completions.ChatCompletionMessageParam[],
    modelId: string,
    minResponseTokens: number,
  ): OpenAI.Chat.Completions.ChatCompletionMessageParam[] => {
    const maxTokensPerRequest = maxTokensByModelId[modelId]
    if (maxTokensPerRequest) {
      const tokenCount =
        getTokenCountForMessages(originalMessages) + minResponseTokens

      if (tokenCount > maxTokensPerRequest) {
        const overage = tokenCount - maxTokensPerRequest

        const trimmedMessages = trimTokens(originalMessages, overage)

        const updatedTokenCount =
          getTokenCountForMessages(trimmedMessages) + minResponseTokens

        if (updatedTokenCount > maxTokensPerRequest) {
          throw new Error(
            `Token count (${tokenCount}) exceeds max tokens per request (${maxTokensPerRequest})`,
          )
        }

        return trimmedMessages
      }
    }

    return originalMessages
  }

export type CreateChatClientWithCustomParserParams<TResponseParser> = {
  apiKey?: string
  modelId: string
  modelDefaultParams?: ModelParams
  parse: TResponseParser
  trimTokens?: TrimTokens
  minResponseTokens?: number
  retryStrategy?: {
    shouldRetry?: (error: InstanceType<typeof OpenAI.APIError>) => boolean
    calculateDelay?: (retryCount: number) => number
    maxRetries?: number
    updateModelParams?: (modelParams: ModelParams) => ModelParams
  }
}

export type TrimTokens = (
  messages: OpenAI.Chat.Completions.ChatCompletionMessageParam[],
  overage: number,
) => OpenAI.Chat.Completions.ChatCompletionMessageParam[]

export type CreateChatClientWithDefaultParserParams = Omit<
  CreateChatClientWithCustomParserParams<any>,
  'parse'
>

export type RetryStrategy =
  CreateChatClientWithCustomParserParams<any>['retryStrategy']

type ResponseParserWithRetry<T> = (
  response: OpenAI.Chat.Completions.ChatCompletion,
  retry: Retry<T>,
) => Promise<T>

export type Retry<T> = ({
  feedback,
  updatedModelParams,
}: {
  feedback?: string
  updatedModelParams?: ModelParams
}) => Promise<T>

// TODO: Find a way to get Core.RequestOptions out of 'openai' package
type HTTPMethod = 'get' | 'post' | 'put' | 'patch' | 'delete'
type Headers = Record<string, string | null | undefined>

type OpenAiCoreRequestOptions<Req extends {} = Record<string, unknown>> = {
  method?: HTTPMethod
  path?: string
  query?: Req
  body?: Req
  headers?: Headers
  maxRetries?: number
  stream?: boolean
  timeout?: number
  httpAgent?: any
  signal?: AbortSignal
  idempotencyKey?: string
}

type CallOpenAiWithRetry = (
  request: {
    messages: OpenAI.Chat.Completions.ChatCompletionMessageParam[]
    functions?: OpenAI.Chat.ChatCompletionCreateParams.Function[]
    modelParams?: ModelParams
  },
  options?: OpenAiCoreRequestOptions,
) => Promise<OpenAI.Chat.Completions.ChatCompletion>

type ResponseParserWithoutRetry<T = string> = (
  response: OpenAI.Chat.Completions.ChatCompletion,
) => T

function isResponseParserWithRetry<T>(
  parse: any,
): parse is ResponseParserWithRetry<T> {
  return parse.length === 2
}

// A TypeScript adaptation of https://github.com/openai/openai-cookbook/blob/main/examples/How_to_count_tokens_with_tiktoken.ipynb
// They count a fixed number of tokens to account for the formatting the model uses, but we instead first format the messages the way
// the model will,  and then we count the total tokens for this formatted string.
// We also don't yet support the "name" field.
// They also account for replies, but we do this differently: we have the caller pass in minReponseTokens (optionally)
//  and make sure that request tokens + minResponseTokens < maxTokensPerRequest
const getTokenCountForMessages = (
  messages: OpenAI.Chat.Completions.ChatCompletionMessageParam[],
) =>
  encode(
    messages
      .map((m) => [`role: ${m.role}`, `content: ${m.content}`].join('\n'))
      .join('\n'),
  ).length

const maxTokensByModelId: Record<string, number> = {
  'gpt-3.5-turbo': 4097,
  'gpt-3.5-turbo-0301': 4097,
  'gpt-3.5-turbo-0613': 4097,
  'gpt-3.5-turbo-16k': 16385,
  'gpt-3.5-turbo-16k-0613': 16385,
  'gpt-4': 8192,
  'gpt-4-0314': 8192,
  'gpt-4-0613': 8192,
  'gpt-4-32k': 32768,
  'gpt-4-32k-0314': 32768,
  'gpt-4-32k-0613': 32768,
}

